mapper.select:
select v.*, bc.id as id, bc.mobile AS phone, bc.person AS psersion, bc.price AS price, bc.created_dt AS createdDt, bc.pay_type as payType , bc.person as person, bc.member_num as memberNum from book_customer bc ,( SELECT c.id AS cid, cp.id as pid, c. NAME AS NAME, c.area AS area, cp.start_time AS startTime, cp.end_time AS endTime FROM court_info c, court_product cp WHERE c.id = cp.court_id) v where bc.product_id = v.pid
and bc.mobile = #{phone}
and v.startTime >= '${createdDtQuery}' and v.endTime<= CONCAT('${createdDtQuery}','23:59:59')
and v.area = #{area}
and v.name = #{nameInfo}
ORDER BY name ,startTime asc
